import React, { useState } from 'react';
import './App.css';

function App() {
  const [lat, setLat] = useState("");
  const [long, setLong] = useState("");
  const [isShownAddress, setIsShownAddress] = useState(false);
  const getLocation = () => {
    var geoOps = {
      enableHighAccuracy: true,
      timeout: 10 //10 seconds
    }
    navigator.geolocation.getCurrentPosition(successCallback, errorCallback, geoOps);
  }
  const successCallback = (pos: any) => {
    var lat = pos.coords.latitude;
    var long = pos.coords.longitude;
    setLat(lat);
    setLong(long);
    setIsShownAddress(true);
  }
  const errorCallback = (e: any) => {
    console.log("error", e)
  }
  return (
    <div className="App">
      <button onClick={() => isShownAddress ? setIsShownAddress(false) : getLocation()}>{isShownAddress ? "Hide" : "Show"} Location</button>
      {
        isShownAddress && <p>You are currnetly at Lat: {lat} , Long: {long}</p>
      }
    </div>
  );
}

export default App;
